INSERT INTO events SELECT NULL as id, name, location, CONCAT(CONCAT(ADDDATE(CURDATE(), INTERVAL (7+dayOfWeek-DAYOFWEEK(CURDATE())) DAY), " "), startTime) AS start, CONCAT(CONCAT(ADDDATE(CURDATE(), INTERVAL (7+dayOfWeek-DAYOFWEEK(CURDATE())) DAY), " "), endTime) AS end, 0 as allDay, description FROM weekly_meeting;
